package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class ArrayLength extends Exp {  public Exp arrayExpr;  public ArrayLength(Exp ae) {    arrayExpr = ae;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}