package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class Assign implements Statement {  public Identifier varId;  public Exp valueExpr;  public Assign(Identifier ai, Exp ae) {    varId = ai;    valueExpr = ae;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}