package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class Assign implements Statement {  public Identifier i;  public Exp e;  public Assign(Identifier ai, Exp ae) {    i = ai;    e = ae;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}