package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class ClassDeclSimple implements ClassDecl {  public Identifier classId;  public VarDeclList fieldVarList;  public MethodDeclList methodList;  public ClassDeclSimple(Identifier ai, VarDeclList avl, MethodDeclList aml) {    classId = ai;    fieldVarList = avl;    methodList = aml;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}