package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class ClassDeclSimple implements ClassDecl {  public Identifier i;  public VarDeclList vl;  public MethodDeclList ml;  public ClassDeclSimple(Identifier ai, VarDeclList avl, MethodDeclList aml) {    i = ai;    vl = avl;    ml = aml;  }  public void accept(Visitor v) {    v.visit(this);  }  public void accept(TypeVisitor v) {    v.visit(this);  }}