package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class Formal {  public Type paramType;  public Identifier paramId;  public Formal(Type at, Identifier ai) {    paramType = at;    paramId = ai;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}