package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class MainClass {  public Identifier classNameId, argId;  public Statement mainStmt;  public MainClass(Identifier ai1, Identifier ai2, Statement as) {    classNameId = ai1;    argId = ai2;    mainStmt = as;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}