package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class MethodDecl {  public Type methodReturnT;  public Identifier methodNameId;  public FormalList formalParamList;  public VarDeclList fieldVarList;  public StatementList statementList;  public Exp returnExpr;  public MethodDecl(Type at, Identifier ai, FormalList afl, VarDeclList avl, StatementList asl, Exp ae) {    methodReturnT = at;    methodNameId = ai;    formalParamList = afl;    fieldVarList = avl;    statementList = asl;    returnExpr = ae;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}