package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class NewArray extends Exp {  public Exp sizeExpr;  public NewArray(Exp ae) {    sizeExpr = ae;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}