package analysis.syntaxtree;import java.util.List;import java.util.Vector;public class StatementList {   private Vector list;   public StatementList() {      list = new Vector();   }   public void addElement(Statement n) {      list.addElement(n);   }   public Statement elementAt(int i)  {       return (Statement)list.elementAt(i);    }   public int size() {       return list.size();    }   public List<Statement> getList() {     return list;   }}