package analysis.syntaxtree;import analysis.visitors.TypeVisitor;import analysis.visitors.Visitor;public class While implements Statement {  public Exp booleanExpr;  public Statement stmt;  public While(Exp ae, Statement as) {    booleanExpr = ae;    stmt = as;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}